  private static final ProductHuntPost testablePost = gson.fromJson(JSON, ProductHuntPost.class);

  @Test
  public void screenshotParsedCorrectly() {
    Screenshot screenshot = testablePost.getScreenshot();
    assertThat(screenshot.getUrl300px())
            .isEqualTo("https://api.url2png.com/v6/P5329C1FA0ECB6/53aa581a482ad87097a506e37c99b23a/png/?thumbnail_max_width=300&amp;url=https%3A%2F%2Fbuffer.academy%2F");
    assertThat(screenshot.getUrl850px())
            .isEqualTo("https://api.url2png.com/v6/P5329C1FA0ECB6/dbfe0ec6417c3ba512a63cf6a1ac5e95/png/?thumbnail_max_width=850&amp;url=https%3A%2F%2Fbuffer.academy%2F");
  }

  @Test
  public void thumbnailParsedCorrectly() {
    Thumbnail thumbnail = testablePost.getThumbnail();
    assertThat(thumbnail.getImageUrl())
            .isEqualTo("https://ph-files.imgix.net/e0164779-b476-4ba9-8a64-93463ba32491?auto=format&amp;fit=crop&amp;h=570&amp;w=430");
    assertThat(thumbnail.getMediaType())
            .isEqualTo("image");
    assertThat(thumbnail.getThumbnailId())
            .isEqualTo(317658);
  }

  @Test
  public void postParsedCorrectly() {
    assertThat(testablePost.getName())
            .isEqualTo("Social Media Academy by Buffer");
    assertThat(testablePost.getCategoryId())
            .isEqualTo(1);
    assertThat(testablePost.getDiscussionUrl())
            .isEqualTo("https://www.producthunt.com/posts/social-media-academy-by-buffer?utm_campaign=producthunt-api&amp;utm_medium=api&amp;utm_source=Application%3A+What%27s+new+on+PH+%28ID%3A+3678%29");
    assertThat(testablePost.getRedirectUrl())
            .isEqualTo("https://www.producthunt.com/r/5c84c47c898177/88968?app_id=3678");
    assertThat(testablePost.getVotesCount())
            .isEqualTo(53);
    assertThat(testablePost.getPostId())
            .isEqualTo(88968);
  }
